elect GROUP_CONCAT(`id`) from `np_files` where `np_files`.`filename` in ('9-P3-C0003-6177966805.jpg')), '1', 4, 1, SYSDATE(), '2019-05-30 00:00:00', SYSDATE(), 0, '{}');
